local p=game:GetService("Players").LocalPlayer local RS=game:GetService("RunService") local UIS=game:GetService("UserInputService") local cs=16 local id=false local rsc=nil local sl=nil local sf=nil local ss=nil local sg=nil if p:FindFirstChild("PlayerGui") and p.PlayerGui:FindFirstChild("SpeedChangerGUI") then return end sg=Instance.new("ScreenGui") sg.Name="SpeedChangerGUI" sg.Parent=p.PlayerGui sg.ResetOnSpawn=false local function sgf() local c=p.Character or p.CharacterAdded:Wait() local h=c:WaitForChild("Humanoid") local a=h:WaitForChild("Animator") local spf=sg:FindFirstChild("SpeedFrame") if not spf then spf=Instance.new("Frame") spf.Name="SpeedFrame" spf.Size=UDim2.new(0,250,0,100) spf.Position=UDim2.new(0.5,-125,0.8,-50) spf.BackgroundColor3=Color3.fromRGB(30,30,30) spf.BorderSizePixel=0 spf.Parent=sg sl=Instance.new("TextLabel") sl.Name="SpeedLabel" sl.Size=UDim2.new(1,0,0.3,0) sl.Position=UDim2.new(0,0,0,0) sl.BackgroundTransparency=1 sl.TextColor3=Color3.new(1,1,1) sl.Font=Enum.Font.SourceSans sl.TextScaled=true sl.Parent=spf ss=Instance.new("TextButton") ss.Name="SpeedSlider" ss.Size=UDim2.new(1,-20,0.3,0) ss.Position=UDim2.new(0,10,0.5,0) ss.BackgroundColor3=Color3.fromRGB(60,60,60) ss.Text="" ss.AutoButtonColor=false ss.Parent=spf sf=Instance.new("Frame") sf.Name="SpeedFill" sf.Position=UDim2.new(0,0,0,0) sf.BackgroundColor3=Color3.fromRGB(0,170,255) sf.BorderSizePixel=0 sf.Parent=ss else sl=spf:FindFirstChild("SpeedLabel") ss=spf:FindFirstChild("SpeedSlider") sf=ss:FindFirstChild("SpeedFill") end sl.Text="Speed: "..math.floor(cs) sf.Size=UDim2.new(math.clamp((cs-8)/492,0,1),0,1,0) local function ufsx(x) local rx=math.clamp((x-ss.AbsolutePosition.X)/ss.AbsoluteSize.X,0,1) sf.Size=UDim2.new(rx,0,1,0) cs=8+rx*492 sl.Text="Speed: "..math.floor(cs) end ss.InputBegan:Connect(function(i) if i.UserInputType==Enum.UserInputType.MouseButton1 then id=true ufsx(i.Position.X) end end) UIS.InputChanged:Connect(function(i) if id and i.UserInputType==Enum.UserInputType.UserInputType.MouseMovement then ufsx(i.Position.X) end end) UIS.InputEnded:Connect(function(i) if i.UserInputType==Enum.UserInputType.MouseButton1 then id=false end end) if rsc then rsc:Disconnect() end rsc=RS.RenderStepped:Connect(function() if p.Character and p.Character:FindFirstChild("Humanoid") then p.Character.Humanoid.WalkSpeed=cs end end) local function aeb(bt,aid,yp) local ef=sg:FindFirstChild(bt.."EmoteFrame") if not ef then ef=Instance.new("Frame") ef.Name=bt.."EmoteFrame" ef.Size=UDim2.new(0,150,0,50) ef.Position=UDim2.new(0,10,0,yp) ef.Parent=sg local eb=Instance.new("TextButton") eb.Name=bt.."EmoteButton" eb.Size=UDim2.new(1,0,1,0) eb.Text="Play "..bt eb.Parent=ef local ea=Instance.new("Animation") ea.AnimationId="rbxassetid://"..aid local eat=a:LoadAnimation(ea) local ip=false eb.MouseButton1Click:Connect(function() ip=not ip if ip then eat:Play() eb.Text="Stop "..bt else eat:Stop() eb.Text="Play "..bt end end) end end local function atb(x,y,z,bt,yp) local tpf=sg:FindFirstChild(bt.."Frame") if not tpf then tpf=Instance.new("Frame") tpf.Name=bt.."Frame" tpf.Size=UDim2.new(0,150,0,50) tpf.Position=UDim2.new(0,10,0,yp) tpf.Parent=sg local tpb=Instance.new("TextButton") tpb.Name=bt.."Button" tpb.Size=UDim2.new(1,0,1,0) tpb.Text=bt tpb.Parent=tpf tpb.MouseButton1Click:Connect(function() if p.Character then p.Character:SetPrimaryPartCFrame(CFrame.new(x,y,z)) end end) end end aeb("L-Dance","18934307172",5) aeb("Duck","107496292201856",60) atb(-3139,80,-35,"Box",115) atb(-719,6,4,"Spawn",170) end sgf() p.CharacterAdded:Connect(function() task.wait(0.1) sgf() end)
